hudson.tasks.Shell.command:
rm -rf service-layer;
cp -rf /projects/www/service-layer ./;

mysql -u root -pabn -e "DROP DATABASE planeonline_ci";
mysql -u root -pabn -e "CREATE SCHEMA planeonline_ci DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci";
mysql -u root -pabn -e "GRANT ALL ON planeonline_ci.* TO 'planeuser_ci'@'localhost' IDENTIFIED BY 'planepass'";

mysql -u root -pabn -e "DROP DATABASE planeonline_ci_test";
mysql -u root -pabn -e "CREATE SCHEMA planeonline_ci_test DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci";
mysql -u root -pabn -e "GRANT ALL ON planeonline_ci_test.* TO 'planeuser_cit'@'localhost' IDENTIFIED BY 'planepass'";

cd service-layer/app;
/opt/phinx/bin/phinx migrate -e ci;
/opt/phinx/bin/phinx migrate -e ci_test;

cd ../tests;
phpunit --coverage-html /projects/jenkins/jobs/service-layer/workspace/codecoverage --coverage-clover /projects/jenkins/jobs/service-layer/workspace/codeclover.xml;
cd behat/plane;
behat -o /projects/jenkins/jobs/service-layer/workspace/report.html
